INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190527, 'Oxtriphylline', 'Moricizine', 'Minor', 'Moricizine may increase the plasma clearance of theophylline by 46% to 68% and decrease theophylline half-life by 20% to 34%. Conversely, theophylline toxicity may occur when moricizine is discontinued. The mechanism is unknown, but may be related to hepatic enzyme induction. Management consists of monitoring the patient for theophylline efficacy, checking levels if indicated, and adjusting theophylline dosage accordingly. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190755/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Pieniaszek HJ Jr, Davidson AF, Benedek IH "Effect of moricizine on the pharmacokinetics of single-dose theophylline in healthy subjects." Ther Drug Monit 15 (1993): 199-203[3] Siddoway LA, Schwartz SL, Barbey JT, Woosley RL "Clinical pharmacokinetics of moricizine." Am J Cardiol 65 (1990): d21-5; disc. D68-71', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190528, 'Tacrine', 'Moricizine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190756/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767365209);
