INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190588, 'Ferrous gluconate', 'Zinc gluconate', 'Minor', 'Iron and zinc may reduce each other''s absorption when they are coadministered orally. The mechanism of interaction is unknown. In clinical studies, concurrent supplementation with iron and zinc was associated with reduced efficacy in improving the growth and iron and zinc status of malnourished infants compared to supplementation with either one alone. In any case, the clinical significance is probably minimal in healthy individuals. However, monitoring for reduced therapeutic response may be advisable in malnourished patients. Limited data suggest that the magnitude of the interaction may be lessened by separating the times of administration of each by 12 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190816/', '[1] Sreedhar B "Conflicting evidence of iron and zinc interactions in humans: does iron affect zinc absorption?" Am J Clin Nutr 78 (2003): 1226; author reply 1226-7[2] Lind T, Lonnerdal B, Stenlund H, et al. "A community-based randomized controlled trial of iron and zinc supplementation in Indonesian infants: interactions between iron and zinc." Am J Clin Nutr 77 (2003): 883-90', 1767365209);
