INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190745, 'Fosnetupitant', 'Avanafil', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of avanafil, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190973/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[3] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[4] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[5] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190746, 'Fosnetupitant', 'Avapritinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9. Increased exposure to avapritinib may increase the risk and/or severity of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils, and platelets.', NULL, 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the starting dosage of avapritinib from 300 mg orally once daily to 100 mg orally once daily.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190974/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation, Cambridge, MA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190747, 'Fosnetupitant', 'Axitinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Otherwise, patients should be monitored closely for development of toxicity such as hypertension/hypertensive crisis, arterial and venous thromboembolic complications, hemorrhage, gastrointestinal perforation or fistula, thyroid dysfunction, reversible posterior leukoencephalopathy syndrome, proteinuria, and liver enzyme elevations or hepatic impairment.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190975/', '[1] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190748, 'Fosnetupitant', 'Betamethasone', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy. The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190976/', '[1] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc, Woodbridge Township, NJ.[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190749, 'Fosnetupitant', 'Bexarotene', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may theoretically increase the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. Conversely, bexarotene appears to be an inducer of CYP450 3A4 and may reduce the plasma concentrations of CYP450 3A4 inhibitors that are also substrates of the isoenzyme, such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190977/', '[1] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[2] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190750, 'Fosnetupitant', 'Binimetinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib. The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors. Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190978/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Mektovi (binimetinib)." Array BioPharma Inc., Boulder, CO.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190751, 'Fosnetupitant', 'Boceprevir', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant. When a single 300 mg-0.5 mg dose of netupitant-palonosetron was administered following 400 mg once daily administration of the potent CYP450 3A4 inhibitor ketoconazole for 12 days, mean netupitant peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 25% and 140%, respectively, while mean Cmax and AUC of palonosetron increased by 15% and 10%, respectively. No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190979/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190752, 'Fosnetupitant', 'Bosentan', 'Moderate', 'Coadministration of bosentan with a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 may result in increased plasma concentrations of bosentan and decreased plasma concentrations of the other drug. Bosentan itself is a substrate and inducer of both CYP450 2C9 and 3A4. Theoretically, bosentan may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', NULL, 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered.', NULL, 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered. Clinical and/or laboratory monitoring may be appropriate whenever bosentan is added to or withdrawn from therapy, and the dosage of the concomitant drug adjusted as necessary. The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with combination CYP450 2C9/3A4 inhibitors (e.g., delavirdine, imatinib, miconazole, mifepristone, voriconazole) should probably be avoided also, if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190980/', '[1] "Product Information. Tracleer (bosentan)." Acetelion Pharmaceuticals US, Inc, South San Francisco, CA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190753, 'Fosnetupitant', 'Bosutinib', 'Major', 'Coadministration with potent and moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bosutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of bosutinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of bosutinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of bosutinib during and for 2 weeks after treatment with itraconazole. If use of a potent or moderate CYP450 3A4 inhibitor is required, an interruption or a dosage reduction of bosutinib therapy should be considered.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190981/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Abbas R, Hug BA, Leister C, Sonnichsen D "A randomized, crossover, placebo- and moxifloxacin-controlled study to evaluate the effects of bosutinib (SKI-606), a dual Src/Abl tyrosine kinase inhibitor, on cardiac repolarization in healthy adult subjects." Int J Cancer 131 (2012): Epub[7] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365209);
