INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190785, 'Fosnetupitant', 'Dexamethasone', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may significantly increase the plasma concentrations of dexamethasone. The proposed mechanism is netupitant inhibition of dexamethasone metabolism via CYP450 3A4.', NULL, 'Dexamethasone should be administered at a reduced dosage when used with netupitant or fosnetupitant.', NULL, 'Dexamethasone should be administered at a reduced dosage when used with netupitant or fosnetupitant. For the prevention of acute and delayed nausea and vomiting associated with initial and repeat courses of cancer chemotherapy, dexamethasone should be dosed according to the product labeling for netupitant-palonosetron or fosnetupitant-palonosetron.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191013/', '[1] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc, Woodbridge Township, NJ.[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.', 1767365209);
