INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190832, 'Guanfacine', 'Fosnetupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme. The risk of adverse reactions such as hypotension, bradycardia, and sedation may increase.', NULL, 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors.', NULL, 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors. For extended-release guanfacine, the manufacturers recommend reducing the dosage to half the recommended level during concomitant use . Further dosage adjustments may be required based on patient tolerance and response.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191060/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.[3] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190833, 'Hydrocodone', 'Fosnetupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of hydrocodone, which is substantially metabolized by the isoenzyme. Increased hydrocodone concentrations could conceivably increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Because hydrocodone is also partially metabolized by CYP450 2D6, the magnitude of interaction may be even greater with concomitant use of a CYP450 3A4 and a CYP450 2D6 inhibitor, or concomitant use of a drug that is a dual inhibitor of both isoenzymes.', NULL, 'Extreme caution is advised if hydrocodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.', NULL, 'Extreme caution is advised if hydrocodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.g., azole antifungal agents, protease inhibitors, aprepitant, ceritinib, ciprofloxacin, chloramphenicol, clarithromycin, cobicistat, conivaptan, crizotinib, delavirdine, diltiazem, dronedarone, erythromycin, fusidic acid, idelalisib, imatinib, letermovir, mibefradil, mifepristone, nefazodone, netupitant, quinupristin-dalfopristin, telithromycin, verapamil) or weak inhibitors that also inhibit CYP450 2D6 (e.g., abiraterone, amiodarone, cimetidine, pazopanib, ranolazine). A fatal overdose may occur following the initiation of a CYP450 3A4 inhibitor in patients already receiving hydrocodone. Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension. Following discontinuation of the CYP450 3A4 inhibitor, patients should be monitored for 