INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190924, 'Fosnetupitant', 'Selumetinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3. When coadministered with itraconazole, a potent CYP450 3A4 inhibitor, selumetinib peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 19% and 49%, respectively.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, a reduction in the dosage of selumetinib is recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191152/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190925, 'Fosnetupitant', 'Sildenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of sildenafil should be considered.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for sildenafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191153/', '[1] "Product Information. Revatio (sildenafil)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] Warrington JS, Shader RI, vonMoltke LL, Greenblatt DJ "In vitro biotransformation of sildenafil (Viagra): Identification of human cytochromes and potential drug interactions." Drug Metab Disposition 28 (2000): 392-7[4] Hyland R, Roe GH, Jones BC, Smith DA "Identification of the cytochrome P450 enzymes involved in the N-demethylation of sildenafil." Br J Clin Pharmaacol 51 (2001): 239-48[5] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190926, 'Fosnetupitant', 'Silodosin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4. Pharmacologic response to silodosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the silodosin dosage adjusted as necessary. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191154/', '[1] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.', 1767365209);
