        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1438011, 'Ipratropium (nasal)', 'Umeclidinium', 'Minor', 'Theoretically, the potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (new onset or exacerbation) when ipratropium nasal spray is used with other agents that possess anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide). However, due to the poor systemic absorption of ipratropium when administered nasally, the interaction would be unlikely at regularly recommended dosages.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
