INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191222, 'Digitoxin', 'Calcium glucoheptonate', 'Major', 'The administration of parenteral calcium preparations, particularly by rapid IV injection, may precipitate serious cardiac arrhythmias in digitalized patients. The mechanism is probably related to the additive or synergistic inotropic effects of calcium and digitalis glycosides on the myocardium.', NULL, 'Intravenous calcium should preferably not be used in patients receiving digitalis glycosides, or it should be given slowly or in small amounts to avoid high serum calcium concentrations.', NULL, 'Intravenous calcium should preferably not be used in patients receiving digitalis glycosides, or it should be given slowly or in small amounts to avoid high serum calcium concentrations. Patients should be monitored for development of arrhythmias.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191450/', '[1] "Product Information. PhosLo (calcium acetate)" Braintree, Braintree, MA.[2] "Product Information. Lanoxin (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Calcium Gluconate (calcium gluconate)." Fresenius Kabi USA, LLC, Lake Zurich, IL.', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191223, 'Hydroflumethiazide', 'Calcium glucoheptonate', 'Moderate', 'Coadministration of thiazide diuretics with high dosages of calcium and/or vitamin D has been associated with reports of hypercalcemia in some patients. Thiazide diuretics inhibit the renal excretion of calcium and may also enhance responsiveness of bone and renal tubule to parathyroid hormone, thus concurrent use of large amounts of calcium or vitamin D can lead to excessively high plasma levels of calcium. Patients who are particularly susceptible include those with hyperparathyroidism, those being treated for osteoporosis, and those receiving high dosages of vitamin D for hypoparathyroidism. Metabolic alkalosis and the milk-alkali syndrome have been reported during prolonged therapy with thiazide diuretics and calcium.', NULL, 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.', NULL, 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider. Serum calcium should be monitored if thiazide diuretics are coadministered with high dosages of calcium and/or vitamin D. Patients should be advised to seek medical attention if they experience signs and symptoms of hypercalcemia such as dizziness, weakness, lethargy, headache, myalgia, anorexia, nausea, vomiting, and seizures.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191451/', '[1] Drinka PJ, Nolten WE "Hazards of treating 