INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191285, 'Halothane', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191513/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191286, 'Halothane', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', NULL, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191514/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Serentil (mesoridazine)" Boehringer-Ingelheim, Ridgefield, CT.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191287, 'Halothane', 'Metocurine', 'Moderate', 'Inhalation anesthetics may potentiate the effects of nondepolarizing muscle relaxants. Long-acting muscle relaxants such as pancuronium and d-tubocurarine are more affected than other agents.', NULL, 'The muscle relaxant dosage may need to be reduced when inhalation anesthetics are used, especially during longer procedures.', NULL, 'The muscle relaxant dosage may need to be reduced when inhalation anesthetics are used, especially during longer procedures. In general, inhalation anesthetics should be administered only by health care providers specially trained in the use of these agents and in the care of anesthetized patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191515/', '[1] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[2] Duncalf D, Nagashima H, Hollinger I, Badola RP, Kaplan R, Foldes FF "Relaxation with ORG-NC45 during enflurane anesthesia." Anesthesiology 55 (1981): a203[3] Withington DE, Donati F, Bevan DR, Varin F "Potentiation of atracurium neuromuscular blockade by enflurane: time-course of effect." Anesth Analg 72 (1991): 469-73[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Swen J, Rashkovsky OM, Ket JM, Koot HW, Hermans J, Agoston S "Interaction between nondepolarizing neuromuscular blocking agents and inhalational anesthetics." Anesth Analg 69 (1989): 752-5[6] "Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical, Abbott Park, IL.[7] Miller RD, Eger EI, Way WL, Stevens WC, Dolan WM "Comparative neuromuscular effects of forane and halothane alone and in combination with d-tubocurarine in man." Anesthesiology 35 (1971): 38-42[8] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[9] Ramsey FM, White PA, Stullken EH, Allen LL, Roy RC "Enflurane potentiation of neuromuscular blockade by atracurium." Anesthesiology 57 (1982): a255', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191288, 'Halothane', 'Nitrous oxide', 'Minor', 'Coadministration of 70% nitrous oxide reduces the dosage requirements for halogenated inhalation anesthetics. Minimum alveolar concentration (MAC) values can be reduced from 0.75% to 0.29% for halothane, from 1.68% to 0.6% for enflurane, and from 1.15% to 0.5% for isoflurane. The MAC equivalent dose for sevoflurane is reduced by approximately 50% in the presence of 50% nitrous oxide.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191516/', '[1] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[2] "Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363027);
