INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191317, 'Heparin', 'Alemtuzumab', 'Moderate', 'Coadministration of alemtuzumab with antiplatelet or anticoagulant drugs may increase the risk of bleeding. Use of alemtuzumab alone has been associated with autoimmune and hemolytic anemia, thrombocytopenia, prolonged myelosuppression, bone marrow aplasia, hemophilia, and pancytopenia. The mechanism for this interaction has not been clearly delineated, but is presumably due to additive antiplatelet/anticoagulant effects.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs. Patients should be monitored for increased anticoagulant effects and bleeding complications. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191545/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Campath (alemtuzumab)" Berlex, Richmond, CA.[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191318, 'Anisindione', 'Heparin', 'Major', 'Although therapy with heparin and oral anticoagulants often overlap in clinical practice, the potential for additive anticoagulant effects and increased risk of severe bleeding should be considered. Oral anticoagulants may prolong the activated partial thromboplastin time (aPTT) in patients receiving heparin, while heparin may prolong the International Normalized Ratio (INR) in patients receiving warfarin.', NULL, 'During concurrent or overlapping therapy, close clinical and laboratory observation for hematologic complications is recommended.', NULL, 'During concurrent or overlapping therapy, close clinical and laboratory observation for hematologic complications is recommended. To minimize the interference of heparin on INR determinations, blood should not be drawn until at least 5 hours after the last intravenous heparin dose or 24 hours after the last subcutaneous heparin dose.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191546/', '[1] "Product Information. Coumadin (warfarin)." DuPont Pharmaceuticals, Wilmington, DE.[2] "Product Information. Heparin Sodium (heparin)." Lilly, Eli and Company, Indianapolis, IN.[3] Guo LQ, Yamazoe Y "Inhibition of cytochrome P450 by furanocoumarins in grapefruit juice and herbal medicines." Acta Pharmacol Sin 25 (2004): 129-36[4] Bodiford AB, Kessler FO, Fermo JD, Ragucci KR "Elevated international normalized ratio with the consumption of grapefruit and use of warfarin." SAGE Open Med Case Rep 0 (2013): 1-3[5] Suvarna R, Pirmohamed M, Henderson L "Possible interaction between warfarin and cranberry juice." 