INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191328, 'Heparin', 'Drotrecogin alfa', 'Major', 'The concurrent administration of therapeutic doses of heparin (greater than 15 units/kg/hour) and drotrecogin alfa may increase the risk of serious or life-threatening bleeding due to additive or synergistic effects on the clotting cascade. Drotrecogin alfa inactivates blood clotting Factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT). Most severely ill septic patients are already at a high risk of bleeding due to coagulopathies with prolonged APTT and prothrombin times (PT).', NULL, 'When making the decision to administer drotrecogin alfa, the increased risk of bleeding versus the benefits of therapy should be carefully considered in seriously ill sepsis patients who are concurrently receiving therapeutic heparin (greater than 15 units/kg/hour).', NULL, 'When making the decision to administer drotrecogin alfa, the increased risk of bleeding versus the benefits of therapy should be carefully considered in seriously ill sepsis patients who are concurrently receiving therapeutic heparin (greater than 15 units/kg/hour). Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In clinical trials, prophylactic low doses of heparin did not appear to affect safety; however, the efficacy of concurrent drotrecogin alfa and heparin in treating sepsis patients has not been determined.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191556/', '[1] "Product Information. Xigris (drotrecogin alfa)." Lilly, Eli and Company, Indianapolis, IN.', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191329, 'Heparin', 'Hemin', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', NULL, 'The use of hemin with concurrent anticoagulant therapy should be avoided.', NULL, 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191557/', '[1] "Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc, Lebanon, NJ.', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191330, 'Heparin', 'Lepirudin', 'Major', 'The concomitant use of heparin and direct thrombin inhibitors may increase the risk of bleeding complications due to additive or synergistic effects on the clotting cascade. Moreover, since direct thrombin inhibitors are used for the treatment of thrombosis in patients with heparin-induced thrombocytopenia (HIT) and for anticoagulation in patients with or at risk for HIT, heparin would not be indicated in these patients.', NULL, 'Patients with known HIT should not receive any heparin.', NULL, 'Patients with known HIT should not receive any heparin. All formulations of heparin, including heparin lock flush, should be withdrawn prior to and during the treatment of heparin-induced thrombocytopenia. Clinicians should allow sufficient time for heparin''s effect on the activated partial thromboplastin time (aPTT) to decrease prior to initiation of the thrombin inhibitor. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191558/', '[1] "Product Information. Acova (argatroban)" SmithKline Beecham, Philadelphia, PA.[2] "Product Information. Refludan (lepirudin)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767363027);
