INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1438594, 'Vitamin A', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C8 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 2C8 by selpercatinib.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1438595, 'Vitamin A', 'Tretinoin', 'Major', 'Concomitant use of a retinoid with vitamin A or other retinoids may result in hypervitaminosis A and additive toxicities. Patients may be at increased risk for potentially serious adverse effects such as vision impairment, pseudotumor cerebri (benign intracranial hypertension), mucositis, esophagitis, hepatitis, hypertriglyceridemia, pancreatitis, ileitis, and colitis.', NULL, 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance.', NULL, 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance. Patients should be made aware of vitamin A sources in vitamin supplements, fortified foods and over-the-counter medications, and be advised to contact their physician if they experience signs of hypervitaminosis A such as inflammation or bleeding of the gums or lips; dry, scaly skin; alopecia; pruritus; erythema; headache; vertigo; and nausea. Patients should discontinue the medication and seek immediate medical attention if they develop signs and symptoms of pseudotumour cerebri or hepatotoxicity.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
