INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191737, 'Isoniazid', 'Halofantrine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', NULL, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191965/', '[1] Charbit B, Becquemont L, Lepere B, Peytavin G, Funck-Bretano C "Pharmacokinetic and pharmacodynamic interaction between grapefruit juice and halofantrine." Clin Pharmacol Ther 72 (2002): 514-23[2] Giao PT, de Vries PJ "Pharmacokinetic interactions of antimalarial agents." Clin Pharmacokinet 40 (2001): 343-73[3] "Product Information. Halfan (halofantrine)." GlaxoSmithKline, Philadelphia, PA.[4] Abernethy DR, Wesche DL, Barbey JT, et al. "Stereoselective halofantrine disposition and effect: concentration-related QTc prolongation." Br J Clin Pharmacol 51 (2001): 231-7[5] Baune B, Flinois JP, Furlan V, et al. "Halofantrine metabolism in microsomes in man: major role of CYP 3A4 and CYP 3A5." J Pharm Pharmacol 51 (1999): 419-26[6] Giao PT, de Vries PJ "Pharmacokinetic interactions of antimalarial agents." Clin Pharmacokinet 40 (2001): 343-73[7] "Product Information. Halfan (halofantrine)." GlaxoSmithKline, Philadelphia, PA.[8] Abernethy DR, Wesche DL, Barbey JT, et al. "Stereoselective halofantrine disposition and effect: concentration-related QTc prolongation." Br J Clin Pharmacol 51 (2001): 231-7[9] Charbit B, Becquemont L, Lepere B, Peytavin G, Funck-Bretano C "Pharmacokinetic and pharmacodynamic interaction between grapefruit juice and halofantrine." Clin Pharmacol Ther 72 (2002): 514-23[10] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363027);
