INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191744, 'Isoniazid', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191972/', '[1] "Product Information. Chirocaine (levobupivacaine)" Organon, West Orange, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191745, 'Isoniazid', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191973/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191746, 'Isoniazid', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as amiodarone, atorvastatin, bicalutamide, cyclosporine, danazol, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, zafirlukast, and zileuton.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as amiodarone, atorvastatin, bicalutamide, cyclosporine, danazol, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, zafirlukast, and zileuton. Caution is advised because these agents may also have additive hepatotoxic effects with lomitapide. Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin monitored prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191974/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Self TH, Chrisman CR, Baciewicz AM, Bronze MS "Isoniazid drug and food interactions." Am J Med Sci 317 (1999): 304-11[5] Uragoda CG, Kottegoda SR "Adverse reactions to isoniazid on ingestion of fish with a high histamine content." Tubercle 58 (1977): 83-9[6] "Product Information. INH (isoniazid)." Ciba Pharmaceuticals, Summit, NJ.[7] Smith CK, Durack DT "Isoniazid and reaction to cheese." Ann Intern Med 88 (1978): 520-1[8] Dimartini A "Isoniazid, tricyclics and the ''''cheese reaction''''." Int Clin Psychopharmacol 10 (1995): 197-8', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191747, 'Isoniazid', 'Lonafarnib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', NULL, 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing to or continuing lonafarnib at the starting dosage of 115 mg/m2 twice daily (for patients 12 months of age or older with a body surface area of at least 0.39 m2). Patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since lonafarnib exposures may be increased despite the dosage reduction and the effect on the QT interval is unknown. The previous dosage of lonafarnib may be resumed 14 days after discontinuing the weak CYP450 3A inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191975/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363027);
