INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192067, 'Cabotegravir', 'Magnesium carbonate', 'Moderate', 'INTERVAL: Coadministration with antacids may decrease the bioavailability of oral cabotegravir. The proposed mechanism likely involves a pH-dependent reduction in dissolution and absorption of cabotegravir, which is poorly soluble over a wide pH range. The interaction has not been studied specifically with antacids.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) should be administered at least 2 hours before or 4 hours after the oral cabotegravir dose.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192295/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192068, 'Carteolol', 'Magnesium carbonate', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192296/', '[1] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17[2] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[3] Hong CY, Hu SC, Lin SJ, Chiang BN "Lack of influence of aluminum hydroxide on the bioavailability and beta-adrenoceptor blocking activity of propranolol." Int J Clin Pharmacol Ther Toxicol 23 (1985): 244-6[4] Kirch W, Schafer-Korting M, Axthelm T, Kohler H, Mutschler E "Interaction of atenolol with furosemide and calcium and aluminum salts." Clin Pharmacol Ther 30 (1981): 429-35[5] Laer S, Neumann J, Scholz H "Interaction between sotalol and an antacid preparation." Br J Clin Pharmacol 43 (1997): 269-72[6] Gugler R, Allgayer 