INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192134, 'Trovafloxacin', 'Magnesium citrate', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192362/', '[1] Teng R, Dogolo LC, Willavize SA, Friedman HL, Vincent J "Effect of Maalox and omeprazole on the bioavailability of trovafloxacin." J Antimicrob Chemother 39 Suppl B (1997): 93-7[2] Noyes M, Polk RE "Norfloxacin and absorption of magnesium-aluminum." Ann Intern Med 109 (1988): 168-9[3] Nix DE, Wilton JH, Ronald B, Distlerath L, Williams VC, Norman A "Inhibition of norfloxacin absorption by antacids." Antimicrob Agents Chemother 34 (1990): 432-5[4] "Product Information. Zagam (sparfloxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[5] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[6] "Prod