INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192146, 'Acetohexamide', 'Magnesium hydroxide', 'Moderate', 'INTERVAL: Some antacids have been reported to increase the rate of absorption and, rarely, the extent of absorption of oral sulfonylureas. The hypoglycemic effects of these agents may be altered.', NULL, 'Sulfonylureas should be administered at least two hours before or after antacids when possible.', NULL, 'Sulfonylureas should be administered at least two hours before or after antacids when possible. Regular monitoring of blood sugar is advisable. In addition, patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192374/', '[1] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Prendergast BD "Glyburide and glipizide, second-generation oral sulfonylurea hypoglycemic agents." Clin Pharm 3 (1984): 473-85[3] "Product Information. Micronase (glyburide)." Pharmacia and Upjohn, Kalamazoo, MI.[4] Kivisto KT, Neuvonen PJ "Effect of magnesium hydroxide on the absorption and efficacy of tolbutamide and chlorpropamide." Eur J Clin Pharmacol 42 (1992): 675-80[5] Kolterman OG "Glyburide in non-insulin-dependent diabetes: an update." Clin Ther 14 (1992): 196-213[6] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[7] Kivisto KT, Neuvonen PJ "Enhancement of absorption and effect of glipizide by magnesium hydroxide." Clin Pharmacol Ther 49 (1991): 39-43[8] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[9] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[10] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[11] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[12] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[13] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[14] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[15] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[16] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[17] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192147, 'Bacampicillin', 'Magnesium hydroxide', 'Moderate', 'Coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents with acid neutralizing or reducing effects may decrease the oral bioavailability and plasma concentrations of ampicillin from bacampicillin. The exact mechanism of interaction is unknown but may be related to degradation of the prodrug when gastric pH is increased.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. Preferably, these agents should be avoided during therapy with bacampicillin, or an alternative antibiotic be prescribed if these medications cannot be discontinued. Patients treated with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) may minimize the effects of the interaction with bacampicillin by separating the times of administration by at least 2 hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192375/', '[1] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71[2] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192148, 'Bepridil', 'Magnesium hydroxide', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192376/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192149, 'Cabotegravir', 'Magnesium hydroxide', 'Moderate', 'INTERVAL: Coadministration with antacids may decrease the bioavailability of oral cabotegravir. The proposed mechanism likely involves a pH-dependent reduction in dissolution and absorption of cabotegravir, which is poorly soluble over a wide pH range. The interaction has not been studied specifically with antacids.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) should be administered at least 2 hours before or 4 hours after the oral cabotegravir dose.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192377/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192150, 'Carteolol', 'Magnesium hydroxide', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192378/', '[1] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17[2] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[3] Hong CY, Hu SC, Lin SJ, Chiang BN "Lack of influence of aluminum hydroxide on the bioavailability and beta-adrenoceptor blocking activity of propranolol." Int J Clin Pharmacol Ther Toxicol 23 (1985): 244-6[4] Kirch W, Schafer-Korting M, Axthelm T, Kohler H, Mutschler E "Interaction of atenolol with furosemide and calcium and aluminum salts." Clin Pharmacol Ther 30 (1981): 429-35[5] Laer S, Neumann J, Scholz H "Interaction between sotalol and an antacid preparation." Br J Clin Pharmacol 43 (1997): 269-72[6] Gugler R, Allgayer 