INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192355, 'Sibutramine', 'Mazindol', 'Major', 'Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake. As such, it may potentiate the pharmacologic response to sympathomimetic agents due to additive or synergistic effects. Although the interaction has not been studied with sibutramine, it has been reported with the selective serotonin reuptake inhibitor, fluoxetine.', NULL, 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', NULL, 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192583/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] Barrett J, Meehan O, Fahy T "SSRI and sympathomimetic interaction." Br J Psychiatry 168 (1996): 253[3] Prior FH, Isbister GK, Dawson AH, Whyte IM "Serotonin toxicity with therapeutic doses of dexamphetamine and venlafaxine." Med J Aust 176 (2002): 240-1[4] Walters AM "Sympathomimetic-fluoxetine interaction." J Am Acad Child Adolesc Psychiatry 31 (1992): 565-6[5] Bostwick JM, Brown TM "A toxic reaction from combining fluoxetine and phentermine." J Clin Psychopharmacol 16 (1996): 189-90[6] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[7] "Product Information. Meridia (sibutramine)."