INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192530, 'Typhoid vaccine (live)', 'Nafcillin', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192758/', '[1] UK government "Typhoid. Available from: URL: https://www.gov.uk/government/uploads/system/uploads/attachment_data/file/148512/Green-Book-Chapter-33-dh_125348.pdf." ([cited 2014]):[2] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] CDC. Centers for Disease Control and Prevention "Typhoid & Paratyphoid Fever. Available from: URL: http://wwwnc.cdc.gov/travel/yellowbook/2014/chapter-3-infectious-diseases-related-to-travel/typhoid-and-paratyphoid-fever." ([2013]):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192531, 'Trimetrexate', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192759/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192532, 'Bifidobacterium longum infantis', 'Trimetrexate', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192760/', '[1] Borriello SP, Hammes WP, Holzapfel W, et.al "Safety of probiotics that contain lactobacilli or bifidobacteria." Clin Infect Dis 36 (2003): 775-80[2] Salminen MK, Tynkkynen S, Rautelin H, et al. "Lactobacillus bacteremia during a rapid increase in probiotic use of Lactobacillus rhamnosus GG in Finland." Clin Infect Dis 35 (2002): 1155-60[3] Schlegel L, Lemerle S, Geslin P "Lactobacillus species as opportunistic pathogens in immunocompromised patients." Eur J Clin Microbiol Infect Dis 17 (1998): 887-8[4] Salminen MK, Rautelin H, Tynkkynen S, et al. "Lactobacillus bacteremia, clinical significance, and patient outcome, with special focus on