INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192822, 'Cerivastatin', 'Pectin', 'Moderate', 'The concomitant use of HMG-CoA reductase inhibitors and the fiber pectin may result in increased levels of low-density lipoprotein cholesterol. The beneficial effect of the HMG-CoA reductase inhibitor may be reduced. The mechanism is decreased gastrointestinal absorption by the fiber.', NULL, 'The clinician should consider avoiding coadministration, but if this is not possible the administration times should be separated as much as possible.', NULL, 'The clinician should consider avoiding coadministration, but if this is not possible the administration times should be separated as much as possible. Patients should be monitored for altered cholesterol-lowering effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193050/', '[1] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice increases serum concentrations of atorvastatin and has no effect on pravastatin." Clin Pharmacol Ther 66 (1999): 118-27[4] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[5] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[6] McMillan K "Considerations in the formulary selection of hydroxymethylglutaryl coenzyme a reductase inhibitors." Am J Health Syst Pharm 53 (1996): 2206-14[7] Bober