INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192917, 'Dihydrotachysterol', 'Phenobarbital', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193145/', '[1] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192918, 'Doxorubicin (liposomal)', 'Phenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doxorubicin, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. Close monitoring for potentially reduced efficacy of doxorubicin is recommended if coadministration is required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193146/', '[1] "Product Information. Adriamycin PFS (doxorubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Riggs CE Jr, Engel S, Wesley M, Wiernik PH, Bachur NR "Doxorubicin pharmacokinetics: prochlorperazine and barbiturate effects." Clin Pharmacol Ther 31 (1982): 263[3] "Product Information. Doxil (doxorubicin liposomal)." Sequis Pharmaceuticals Inc, Menlo Park, CA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363027);
