INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193086, 'Glycerol phenylbutyrate', 'Quinidine', 'Moderate', 'Coadministration with glycerol phenylbutyrate may decrease the systemic exposure to and therapeutic efficacy of drugs that are substrates of CYP450 3A4. Glycerol phenylbutyrate, its active moiety phenylbutyrate, and active metabolite phenylacetic acid are considered weak CYP450 3A4 inducers in vivo.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.g., oral midazolam) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., alfentanil, oral contraceptives, quinidine, and cyclosporine) should be done with caution and monitoring for reduced efficacy. Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever glycerol phenylbutyrate is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193314/', '[1] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363027);
