INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193098, 'Quinidine', 'Magaldrate', 'Moderate', 'Coadministration with drugs that can increase urinary pH such as antacids, carbonic anhydrase inhibitors, or urinary alkalinizers may decrease the urinary excretion of quinidine. The proposed mechanism is increased renal tubular reabsorption due to reduced ionization of quinidine in alkaline urine.', NULL, 'Caution is advised if quinidine is used with agents that can increase urinary pH.', NULL, 'Caution is advised if quinidine is used with agents that can increase urinary pH. Quinidine levels may need to be monitored more closely following addition or discontinuation of these agents, and the quinidine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate quinidine toxicity such as tinnitus, hearing loss, visual disturbances, diarrhea, headache, dizziness, palpitations, syncope, or irregular heartbeats.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193326/', '[1] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[2] Mauro VF, Mauro LS, Fraker TD Jr, Temesy-Armos PN, Somani P "Effect of aluminum hydroxide gel on quinidine gluconate absorption." DICP 24 (1990): 252-4[3] Romankiewicz JA, Reidenberg M, Drayer D, Franklin JE "The noninterference of aluminum hydroxide gel with quinidine sulfate absorption: An approach to control quinidine-induced diarrhea." Am Heart J 96 (1978): 518-20[4] Gerhardt RE, Knouss RF, Thyrum PT, et al "Quinidine excretion in aciduria and alkaluria." Ann Intern Med 