INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193225, 'Reserpine', 'Levmetamfetamine (nasal)', 'Moderate', 'Sympathomimetic amines may decrease the hypotensive effect of postganglionic adrenergic blocking agents, while the latter may potentiate the pharmacologic effects of direct-acting sympathomimetic amines (e.g., dobutamine, epinephrine, methoxamine, norepinephrine) but inhibit those that are primarily indirect-acting (e.g., mephentermine). Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings. Therefore, they may sensitize adrenergic receptors to direct-acting sympathomimetics, but blunt the effects of indirect-acting agents whose activity is mediated through the release of catecholamines.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Alternatives to postganglionic adrenergic blocking agents should be considered if patients are treated with sympathomimetic amines, since effects of the latter may be intensified or diminished depending on whether they are direct- or indirect-acting. Most agents with indirect sympathomimetic activity are mixed-acting, thus it may be difficult to predict how they will be affected by postganglionic adrenergic blocking agents. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193453/', '[1] Gulati OD, Dave BT, Gokhale SD, Sh