INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193443, 'Anisindione', 'St. John''s Wort', 'Moderate', 'Limited clinical data suggest that St. John''s wort may reduce the plasma concentrations and/or hypoprothrombinemic effect of warfarin and other oral anticoagulants. The proposed mechanism is induction of CYP450 isoenzymes by constituents of St. John''s wort.', NULL, 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine. Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, these drugs should preferably not be used with St. John''s wort. If they are given together, the INR must be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of St. John''s wort in patients who are stabilized on their anticoagulant regimen.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193671/', '[1] De Smet PA, Touw DJ "Safety of St John''s wort (Hypericum perforatum)." Lancet 355 (2000): 575-6[2] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P4502C9 is the principal catalyst of racemic acenocoumarol hydroxylation reactions in human liver microsomes." Drug Metab Dispos 28 (2000): 1284-90[3] Mauer A, Johne A, Bauer S, et al "Interaction of St. John''s wort extract with phenprocoumon." Eur J Clin Pharmacol 55 (1999): A22[4] Izzo AA, Ernst E "Interactions between herbal medicines and prescribed drugs: a systematic review." Drugs 61 (2001): 2163-75[5] Yue QY, Berquis