INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193444, 'Berotralstat', 'St. John''s Wort', 'Major', 'Coadministration with inducers of P-glycoprotein (P-gp) may significantly decrease the plasma concentrations and pharmacologic effects of berotralstat, a substrate of this efflux transporter.', NULL, 'Due to the potential for diminished pharmacologic effects of berotralstat, its concomitant use with P-gp inducers, including but not limited to rifampin and St.', NULL, 'Due to the potential for diminished pharmacologic effects of berotralstat, its concomitant use with P-gp inducers, including but not limited to rifampin and St. John''s wort, should be avoided. Alternative therapeutic agents with less P-gp induction potential should be considered whenever possible.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193672/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193445, 'Boceprevir', 'St. John''s Wort', 'Major', 'Coadministration with St. John''s wort may significantly decrease the plasma concentrations of boceprevir, a hepatitis C virus NS3/4A protease inhibitor. The proposed mechanism involves induction of boceprevir metabolism via CYP450 3A4. The interaction has not been specifically studied with boceprevir, but has been reported with HIV protease inhibitors.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with St.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with St. John''s wort is considered contraindicated. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193673/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.', 1767363027);
