INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193476, 'Tivozanib', 'St. John''s Wort', 'Major', 'Coadministration of tivozanib with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of tivozanib, which has been shown to be a substrate of the isoenzyme. The clinical effects of strong CYP450 3A4 inducers on repeated daily dosing of tivozanib has not been studied.', NULL, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer such as St.', NULL, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer such as St. John''s wort should be generally avoided. Some authorities consider the concomitant use of tivozanib with St. John''s wort to be contraindicated. It should be noted that the inducing effect of St. John''s wort might persist for at least 2 weeks following discontinuation of treatment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193704/', '[1] "Product Information. Fotivda (tivozanib)." Aveo Pharmaceuticals, Inc., Boston, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363027);
