INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193501, 'Streptomycin', 'Phenylbutazone', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193729/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193502, 'Streptomycin', 'Pipecuronium', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193730/', '[1] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolong