INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193762, 'Drotrecogin alfa', 'Tolmetin', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT).', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia.', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia. Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193990/', '[1] "Product Information. Xigris (drotrecogin alfa)." Lilly, Eli and Company, Indianapolis, IN.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193763, 'Enoxacin', 'Tolmetin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193991/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatif