INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193864, 'Trifluoperazine', 'Physostigmine (ophthalmic)', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/194092/', '[1] "Product Information. Pilopine HS (pilocarpine ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Multum Information Services, Inc. Expert Review Panel"', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (193865, 'Trifluoperazine', 'Probucol', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', NULL, 'Caution and clinical monitoring are recommended.', NULL, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/194093/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[3] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] 