INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440276, 'Insulin human (isophane)', 'Demeclocycline', 'Moderate', 'Tetracyclines may enhance the hypoglycemic effect of insulin by increasing tissue sensitivity to exogenous insulin.', 'DDInter', 1767317476, 'Blood glucose should be monitored closely whenever tetracyclines are prescribed to patients on insulin, especially if they are elderly or have renal impairment. The insulin dosage may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Synergism', 'Blood glucose should be monitored closely whenever tetracyclines are prescribed to patients on insulin, especially if they are elderly or have renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440277, 'Insulin human (isophane)', 'Deserpidine', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the hypoglycemic effects of insulin and insulin secretagogues (e.g., sulfonylureas, meglitinides). Limited data have demonstrated possible hypoglycemic activity with agents such as guanethidine and reserpine. The mechanism may involve depletion of catecholamine stores from adrenergic nerve endings, which can interfere with glycogenolysis and other mechanisms related to regulation of blood glucose levels.', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended when postganglionic adrenergic blocking agents are coadministered with insulin or insulin secretagogues, particularly in patients with advanced age and/or renal impairment. Insulin and oral antidiabetic dosages may require adjustment if an interaction is suspected. Patients should be instructed about the need for regular monitoring of blood glucose levels and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked during antiadrenergic therapy. However, other symptoms such as headache, dizziness, drowsiness, nausea, hunger, and sweating may be unaffected. Patients should be observed for loss of glycemic control when antiadrenergic therapy is withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended when postganglionic adrenergic blocking agents are coadministered with insulin or insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440278, 'Insulin human (isophane)', 'Dexamethasone (topical)', 'Minor', 'The efficacy of insulin and other antidiabetic agents may be diminished by topical corticosteroids, particularly during prolonged or indiscriminate use. Corticosteroids can raise blood glucose level by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis. The duration of administration Close clinical monitoring of glycemic control is recommended if topical corticosteroids are administered chronically and/or to large areas in diabetic patients.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440279, 'Insulin human (isophane)', 'Dexfenfluramine', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317476, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
