INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440391, 'Insulin human (isophane)', 'Paroxetine', 'Moderate', 'The hypoglycemic effect of insulin may be potentiated by certain drugs, including ACE inhibitors, angiotensin receptor blockers (ARBs), 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinidine, quinine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, ARBs, fibrates, ginseng); stimulating insulin secretion (salicylates, disopyramide, pentoxifylline, propoxyphene, quinidine, quinine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Clinical hypoglycemia has been reported during use of some of these agents alone or with insulin and/or insulin secretagogues.', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment. The insulin dosage may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations).', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440392, 'Insulin human (isophane)', 'Pasireotide', 'Moderate', 'Somatostatin analogs may alter the therapeutic response to insulin and other antidiabetic agents. Somatostatin analogs can induce hyperglycemia and, less frequently, hypoglycemia, by inhibiting the secretion of various counter-regulatory hormones involved in glucose homeostasis (i.e., glucagon, insulin, growth hormone, incretin hormones).', 'DDInter', 1767317476, 'Close monitoring of diabetic control is recommended if somatostatin analogs are prescribed to patients with preexisting diabetes. Glycemic status including fasting plasma glucose and/or hemoglobin A1c should be assessed prior to initiation of therapy and periodically during therapy in accordance with manufacturer''s product labeling, and the antidiabetic treatment adjusted as necessary.', 'Antagonism', 'Close monitoring of diabetic control is recommended if somatostatin analogs are prescribed to patients with preexisting diabetes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
