INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440738, 'Insulin human (inhalation, rapid acting)', 'Metreleptin', 'Moderate', 'Since treatment of leptin deficiency in patients with generalized lipodystrophy tends to improve insulin sensitivity and leads to a reduction in food intake, patients receiving metreleptin may require adjustments in their antidiabetic medications. In particular, large dosage reductions of insulin or insulin secretagogue (e.g., sulfonylurea, meglitinide) may be necessary in some patients to minimize the risk of hypoglycemia.', 'DDInter', 1767317476, 'Blood glucose should be closely monitored in patients receiving insulin, especially at high dosages, or insulin secretagogue during concomitant treatment with metreleptin, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitation.', 'Synergism', 'Blood glucose should be closely monitored in patients receiving insulin, especially at high dosages, or insulin secretagogue during concomitant treatment with metreleptin, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
