INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440795, 'Insulin human (inhalation, rapid acting)', 'Rosiglitazone', 'Moderate', 'Coadministration of a thiazolidinedione in combination with insulin may increase the risk of edema compared to insulin alone. The mechanism is unknown but may involve enhancement of the antinatriuretic and/or peripheral vasodilatory effects of insulin.', 'DDInter', 1767317476, 'When used with insulin, the manufacturer recommends that rosiglitazone dosage not exceed 4 mg/day and that patients be monitored for cardiovascular adverse effects. The combination should be discontinued if there is a lack of response (defined as a reduction in HbA1c or insulin dose after 4 to 5 months) or significant adverse effects occur. Patients should be advised to notify their physician immediately if they experience signs and symptoms of heart failure such as fluid retention, edema, rapid weight gain, or shortness of breath. Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations.', 'Synergism', 'When used with insulin, the manufacturer recommends that rosiglitazone dosage not exceed 4 mg/day and that patients be monitored for cardiovascular adverse effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440796, 'Insulin human (inhalation, rapid acting)', 'Safinamide', 'Moderate', 'The hypoglycemic effect of insulin may be potentiated by certain drugs, including ACE inhibitors, angiotensin receptor blockers (ARBs), 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinidine, quinine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, ARBs, fibrates, ginseng); stimulating insulin secretion (salicylates, disopyramide, pentoxifylline, propoxyphene, quinidine, quinine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Clinical hypoglycemia has been reported during use of some of these agents alone or with insulin and/or insulin secretagogues.', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment. The insulin dosage may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations).', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
