INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440830, 'Insulin human (inhalation, rapid acting)', 'Tobramycin', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin. In nondiabetic subjects with mild asthma (n=36) and moderate asthma (n=31), administration of albuterol 30 minutes prior to inhaled human insulin resulted in a mean increase in insulin systemic exposure (AUC).', 'DDInter', 1767317476, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended. Blood glucose should be monitored closely, and the insulin dose adjusted accordingly.', 'Absorption', 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440831, 'Insulin human (inhalation, rapid acting)', 'Tolazamide', 'Moderate', 'Coadministration of a sulfonylurea with insulin may potentiate the risk of hypoglycemia. Elderly, debilitated, or malnourished patients, and those with adrenal or pituitary insufficiency, are particularly susceptible to the hypoglycemic action of glucose-lowering drugs.', 'DDInter', 1767317476, 'Caution and close blood glucose monitoring are advised during coadministration of these agents. A lower dosage of sulfonylurea or insulin may be required. Patients should be counseled to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately and contact their physician. Patients should also be advised to take precautions to avoid hypoglycemia while driving or operating hazardous machinery.', 'Synergism', 'Caution and close blood glucose monitoring are advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
