INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1440985, 'Interferon alfa-2b', 'Niraparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with niraparib.', 'DDInter', 1767317476, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Synergism', 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
