INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441009, 'Interferon alfa-2b', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 1767317476, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441010, 'Interferon alfa-2b', 'Ramelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations and pharmacologic effects of ramelteon, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when ramelteon is used with CYP450 1A2 inhibitors. A reduction in the ramelteon dosage may be necessary in patients who experience excessive sedation or other adverse effects.', 'Metabolism', 'Caution is advised when ramelteon is used with CYP450 1A2 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
