INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441135, 'Interferon beta-1a', 'Natalizumab', 'Major', 'Use of natalizumab in combination with immunomodulatory agents such as interferon beta has resulted in rare cases of progressive multifocal leukoencephalopathy (PML), a severely disabling, potentially fatal opportunistic viral infection of the brain. The mechanism has not been described.', 'DDInter', 1767317476, 'Although data are limited with respect to the risk of PML, the use of natalizumab in combination with immunomodulatory agents including interferon beta should generally be avoided. All patients treated with natalizumab should be monitored closely and the drug discontinued immediately at the first sign or symptom suggestive of PML. However, it is not known if early detection of PML and discontinuation of natalizumab will mitigate the disease.', 'Others', 'Although data are limited with respect to the risk of PML, the use of natalizumab in combination with immunomodulatory agents including interferon beta should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
