INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441217, 'Interferon beta-1a', 'Warfarin', 'Moderate', 'Interferons may increase the plasma concentrations and pharmacologic effects of warfarin and other oral anticoagulants. At least one case of this interaction has been reported in the literature. The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes.', 'DDInter', 1767317476, 'Caution is advised if oral anticoagulants are administered with an interferon. The INR should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Caution is advised if oral anticoagulants are administered with an interferon.', NULL, NULL, 'Moderate', 'pharmacodynamic');
