INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441330, 'Interferon beta-1b', 'Pimozide', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 1A2 may increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of pimozide, including serious adverse effects such as tardive dyskinesia, neuroleptic malignant syndrome and QT interval prolongation, should be considered in patients receiving concomitant CYP450 1A2 inhibitors.', 'DDInter', 1767317476, 'Because excessive prolongation of the QT interval is associated with an increased risk of ventricular arrhythmias such as ventricular tachycardia and torsades de pointes, an ECG should be performed prior to initiating therapy with pimozide and periodically thereafter, especially during dosage adjustment or following the addition of a CYP450 1A2 inhibitor.', 'Metabolism', 'Because excessive prolongation of the QT interval is associated with an increased risk of ventricular arrhythmias such as ventricular tachycardia and torsades de pointes, an ECG should be performed prior to initiating therapy with pimozide and periodically thereafter, especially during dosage adjustment or following the addition of a CYP450 1A2 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
