INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441899, 'Fluticasone', 'Saquinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the systemic exposure to fluticasone following intranasal administration or oral inhalation. Fluticasone undergoes extensive first-pass and systemic metabolism via CYP450 3A4, thus inhibition of the isoenzyme may significantly increase systemic bioavailability of the drug. However, the extent of interaction may depend on route of fluticasone administration and the specific formulation.', 'DDInter', 1767317476, 'Concomitant use of most orally inhaled fluticasone preparations with potent CYP450 3A4 inhibitors is not recommended unless the potential benefit outweighs the risk of systemic side effects. Alternatives to fluticasone should be considered whenever possible. A less potent, less lipophilic, and/or shorter-acting agent such as beclomethasone or flunisolide may be appropriate. The lowest effective dosage of orally inhaled corticosteroid should be used, and further adjustments made as necessary according to therapeutic response and tolerance. Intranasal fluticasone and fluticasone furoate-vilanterol oral inhalation powder may be used cautiously in combination with potent CYP450 3A4 inhibitors except ritonavir.', 'Metabolism', 'Concomitant use of most orally inhaled fluticasone preparations with potent CYP450 3A4 inhibitors is not recommended unless the potential benefit outweighs the risk of systemic side effects.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441900, 'Fluvoxamine', 'Saquinavir', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', 'DDInter', 1767317476, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441901, 'Fosamprenavir', 'Saquinavir', 'Minor', 'Coadministration of saquinavir with amprenavir or fosamprenavir may result in a reduction in the plasma concentrations and therapeutic effects of both drugs. The mechanism of the interaction has not been described.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441902, 'Fosaprepitant', 'Saquinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
