INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441904, 'Fosphenytoin', 'Saquinavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441905, 'Fostamatinib', 'Saquinavir', 'Major', 'Coadministration of fostamatinib with potent inhibitors of CYP450 3A4 may significantly increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. Increased exposure to R406 may result in increased risk of adverse effects such as diarrhea, hepatotoxicity, hypertension, and neutropenia.', 'DDInter', 1767317476, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, NULL, 'Major', 'pharmacodynamic');
