INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1441961, 'Saquinavir', 'Lansoprazole', 'Moderate', 'The concomitant administration of some proton pump inhibitors with saquinavir, with or without ritonavir, may significantly increase exposure to saquinavir. The mechanism is unknown.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring for saquinavir toxicity is recommended during concurrent use of a proton pump inhibitor. According to some authorities, a dose reduction for saquinavir may be considered.', 'Others', 'Clinical and laboratory monitoring for saquinavir toxicity is recommended during concurrent use of a proton pump inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
