INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442034, 'Saquinavir', 'Nitisinone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of and exposure to, nitisinone. The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of nitisinone, which has been shown in in vitro studies to be metabolized by this isoenzyme.', 'DDInter', 1767317476, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors. Pharmacologic response to nitisinone should be monitored whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the nitisinone dosage adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
