INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442090, 'Saquinavir', 'Propafenone', 'Major', 'Coadministration with saquinavir may significantly increase the plasma concentrations of some antiarrhythmic agents such as amiodarone, bepridil, flecainide, propafenone, and quinidine. The mechanism is decreased clearance due to inhibition of CYP450 3A4- and 2D6-mediated metabolism by saquinavir, particularly when coadministered with ritonavir as a pharmacokinetic booster. Although clinical data are lacking, the interaction could conceivably lead to serious and/or life-threatening reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', 'DDInter', 1767317476, 'The use of saquinavir in combination with amiodarone, bepridil, flecainide, propafenone, and quinidine is considered contraindicated.', 'Metabolism', 'The use of saquinavir in combination with amiodarone, bepridil, flecainide, propafenone, and quinidine is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
