INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442198, 'Vilazodone', 'Saquinavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of vilazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The maintenance dosage of vilazodone should be reduced to 20 mg/day when used with potent CYP450 3A4 inhibitors.', 'Metabolism', 'The maintenance dosage of vilazodone should be reduced to 20 mg/day when used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442199, 'Vinblastine', 'Saquinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein may significantly increase the plasma concentrations of vinca alkaloids, which are substrates of both the hepatic microsomal isoenzyme and intracellular efflux transporter.', 'DDInter', 1767317476, 'Concomitant use of vinca alkaloids with potent CYP450 3A4 and/or P-glycoprotein inhibitors should be avoided if possible. Some authorities recommend avoiding concomitant use of vinca alkaloids during and for 2 weeks after treatment with itraconazole. Otherwise, conservative dosing of the antineoplastic should be considered, and the patient closely monitored for toxicity. Based on the known half-life of vinca alkaloids (24 to 48 hours), the time course of interaction is expected to be approximately 5 to 7 days. Patients should be advised to seek medical attention if they experience symptoms that could indicate neuro- or myelotoxicity, including constipation, abdominal pain or bloating, urinary retention, paresthesia, paralysis, muscle weakness, hearing loss, seizures, erratic blood pressure changes, unusual or excessive bleeding, easy bruising, pallor, fatigue, dizziness, lightheadedness, fever, chills, and sore throat. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the antineoplastic dosage is adjusted upward to the previous dosage.', 'Metabolism', 'Concomitant use of vinca alkaloids with potent CYP450 3A4 and/or P-glycoprotein inhibitors should be avoided if possible.', NULL, NULL, 'Major', 'pharmacodynamic');
