INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442475, 'Iopromide', 'Iodide I-123', 'Moderate', 'INTERVAL: Iodine-containing products may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides. These effects may last up to 8 weeks.', 'DDInter', 1767317476, 'Iodine preparations including expectorants, vitamins, topicals, and intravascular iodine-containing contrast media should be withheld for at least 2 to 4 weeks and possibly longer, and cholecystographic agents should be withheld for more than 4 weeks, prior to administration of sodium iodide I-131 or I-123.', 'Others', 'Iodine preparations including expectorants, vitamins, topicals, and intravascular iodine-containing contrast media should be withheld for at least 2 to 4 weeks and possibly longer, and cholecystographic agents should be withheld for more than 4 weeks, prior to administration of sodium iodide I-131 or I-123.', NULL, NULL, 'Moderate', 'pharmacodynamic');
