INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442572, 'Ipilimumab', 'Vemurafenib', 'Moderate', 'Coadministration of vemurafenib with ipilimumab may potentiate the risk of hepatotoxicity.', 'DDInter', 1767317476, 'The safety and effectiveness of vemurafenib in combination with ipilimumab have not been established. Liver transaminases, alkaline phosphatase, and bilirubin should be monitored prior to initiation of vemurafenib treatment and monthly during treatment, or as clinically indicated. Laboratory abnormalities should be managed with dosage reduction, treatment interruption, or treatment discontinuation in accordance with the product labeling.', 'Synergism', 'The safety and effectiveness of vemurafenib in combination with ipilimumab have not been established.', NULL, NULL, 'Moderate', 'pharmacodynamic');
