INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442714, 'Irinotecan', 'Filgrastim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 1767317476, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', 'Antagonism', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442715, 'Irinotecan', 'Fingolimod', 'Major', 'Coadministration of fingolimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of infections. Fingolimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 1767317476, 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration. A complete blood count is recommended prior to starting fingolimod if a recent one (i.e., within last 6 months) is not available.', 'Synergism', 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, NULL, 'Major', 'pharmacodynamic');
