INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442747, 'Isosorbide', 'Irinotecan', 'Moderate', 'Irinotecan-induced vomiting or diarrhea may increase the risk and/or severity of dehydration produced by diuretics.', 'DDInter', 1767317476, 'Concomitant use of diuretics during irinotecan administration or during periods of vomiting or diarrhea that may follow is not recommended.', 'Others', 'Concomitant use of diuretics during irinotecan administration or during periods of vomiting or diarrhea that may follow is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442748, 'Itraconazole', 'Irinotecan', 'Major', 'Coadministration with certain azole antifungal agents may significantly increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 is the isoenzyme responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 1767317476, 'Concomitant use of irinotecan with ketoconazole or itraconazole is considered contraindicated. Ketoconazole should be discontinued for at least one week before initiation of treatment with irinotecan, and itraconazole should be discontinued for up to two weeks before. Some authorities consider concomitant administration of irinotecan to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of irinotecan with ketoconazole or itraconazole is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442749, 'Irinotecan', 'Ivosidenib', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4, and induction of this process results in less of the drug available in the plasma for conversion to SN-38 via carboxylesterases..', 'DDInter', 1767317476, 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers. Pharmacologic response to irinotecan should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the irinotecan dosage adjusted as necessary.', 'Metabolism', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442750, 'Irinotecan', 'Ketoconazole', 'Major', 'Coadministration with certain azole antifungal agents may significantly increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 is the isoenzyme responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 1767317476, 'Concomitant use of irinotecan with ketoconazole or itraconazole is considered contraindicated. Ketoconazole should be discontinued for at least one week before initiation of treatment with irinotecan, and itraconazole should be discontinued for up to two weeks before. Some authorities consider concomitant administration of irinotecan to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of irinotecan with ketoconazole or itraconazole is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442751, 'Irinotecan', 'Lactitol', 'Moderate', 'Because irinotecan has been reported to cause diarrhea in the majority of treated patients, the use of laxatives may not be appropriate during irinotecan therapy.', 'DDInter', 1767317476, 'In patients requiring laxatives for constipation associated with narcotic administration, the standard regimen may be continued under supervision of the clinician. However, patients should be advised to inform their physician of any changes in their bowel habit so that dosage adjustments or discontinuation of laxatives may be done promptly.', 'Synergism', 'In patients requiring laxatives for constipation associated with narcotic administration, the standard regimen may be continued under supervision of the clinician.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442752, 'Irinotecan', 'Lactobacillus acidophilus', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
